import React from 'react'


// component from material-ui
import {Paper, Box, Typography, Button } from '@material-ui/core'
import { makeStyles, useTheme } from '@material-ui/styles'

import Particles from "react-tsparticles";


const useStyles = makeStyles(theme => ({
  root: {
    height: 800,
  },
  typo: {
    [theme.breakpoints.down('xs')]: {
      fontSize: theme.spacing(3.5)
    },
  }
}))


function Home() {

  const classes = useStyles()

  const theme = useTheme()

  return (
    <Paper className={classes.root} id="home">
      <Particles
        id="tsparticles"
        options={{
          background: {
            color: {
              value: theme.palette.type === 'light' ? theme.palette.animation.light.background : theme.palette.animation.dark.background,
            },
          },
          fpsLimit: 60,
          interactivity: {
            detectsOn: "canvas",
            events: {
              onClick: {
                enable: true,
                mode: "push",
              },
              onHover: {
                enable: true,
                mode: "repulse",
              },
              resize: true,
            },
            modes: {
              bubble: {
                distance: 40000,
                duration: 2,
                opacity: 0.8,
                size: 400,
              },
              push: {
                quantity: 4,
              },
              repulse: {
                distance: 2000,
                duration: 0.4,
              },
            },
          },
          particles: {
            color: {
              value: "#bab6b5",
            },
            links: {
              color: "#bab6b5",
              distance: 150,
              enable: true,
              opacity: 0.5,
              width: 1,
            },
            collisions: {
              enable: true,
            },
            move: {
              direction: "none",
              enable: true,
              outMode: "bounce",
              random: false,
              speed: 6,
              straight: false,
            },
            number: {
              density: {
                enable: true,
                value_area: 800,
              },
              value: 80,
            },
            opacity: {
              value: 0.5,
            },
            shape: {
              type: "circle",
            },
            size: {
              random: true,
              value: 5,
            },
          },
          detectRetina: true,
        }}
      />
    <Box
    display="flex"
    flexDirection="column"
    justifyContent="center"
    alignItems="center"
    minHeight="100vh"
    style={{position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)'}}
    >
    <Typography variant="h3" align="center" className={classes.typo}>
     Hello, I'm <span style={{color:"red"}}>Osama Khan</span><br></br>I am a software developer
    </Typography>  
    <Button variant="outlined" href="#about" color="inherit">View my Work </Button>
  </Box>

    </Paper>
  )
}

export default Home
