import React from 'react'

// MuiComponent
import {Dialog as MuiDialog} from '@material-ui/core'
import { DialogTitle } from '@material-ui/core'
import {DialogContent} from '@material-ui/core'
import {Typography} from '@material-ui/core'
import {Grid} from '@material-ui/core'
import {TextField as MuiTextField} from '@material-ui/core'
import { Button } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'
import {GlobalContext} from '../../ContextApi'

const useStyles = makeStyles(theme=> ({
  paper: {
    position: 'absolute',
    top: 0,
  },
  input : {
    '& > *':{
      margin: theme.spacing(1)
    },
    '& form > *':{
      margin: theme.spacing(1)
    }

  },

  closeButton : {
    marginLeft: '90%',
    fontSize: '1.2rem'
  },

  skills : {
    fontWeight: 800,
    opacity: 0.8
  }

}))



const TextField = ({...others}) => {
  return (
    <MuiTextField 
      size="small"
      variant="outlined"
      {...others}
    />
  )
}

const InputButton = ({children, ...others}) => {
  return (
    <Button 
      variant="contained"
      component="label"
      color='primary'
      {...others}
    >
      {children}
    </Button>
  )
}






const initialSkill = {
  skill_name: '',
  skill_proficiency: ''
}

const initialError = {
  error: false,
  index: -1,
  label:'',
  text: ''
}


function Dialog({open, setClose}) {
  const {profile, loading} = GlobalContext()
  const [profileInput, setProfileInput] = React.useState(profile)
  const [skill, setSkill] = React.useState(initialSkill)
  const [error, setError] = React.useState(initialError)
  let demo  = profile


  const classes = useStyles()


  const handleChange =  (e, index) => {

    const {name} = e.target
    let value = ''
    if (name === 'image' || name === 'resume') {
      value = e.target.files[0]
    } else {
      value = e.target.value
    }
    if (name.includes('skill', 0)) {
      let tempProfileInput = {...profileInput}
      tempProfileInput.skills[index] = {...tempProfileInput.skills[index], [name.substr(6)]: value}
      setProfileInput(tempProfileInput)

      // setSkill(prevState => {
      //   return {...prevState, [name]: value}
      // })
    } else {

      setProfileInput(prevState => {
      return {...prevState, [name]: value}
    })
    }
  }

  const handleNumber = (e, index) => { 
    console.log(e.target.value)
    if (e.target.value !== '' ) {
      handleChange(e, index)
    }
  }



  React.useEffect(() => {
    setProfileInput(profile)
  }, [loading])

  // console.log(
  //   profileInput)

  return (
    <MuiDialog 
      open={open}
      maxWidth="sm"
      onClose={()=>setClose(true)}
      classes = {{paper: classes.paper}}
    >
      <DialogTitle>
        <Button
         align="right"
         color='secondary'
         className={classes.closeButton}
         onClick={()=>setClose(false)}
        >
          X
        </Button>
      </DialogTitle>

      <DialogContent>
        <Grid container justify="center" >
              <Grid item xs={12} className={classes.input}>
                <TextField 
                  fullWidth
                  name="name"
                  label="name"  
                  value={profileInput.name}
                  onChange={handleChange}
                />
                <TextField 
                  fullWidth
                  name="description"
                  label="description"  
                  value={profileInput.description}
                  onChange={handleChange}
                  multiline
                  rows={5}
                />

                <InputButton
                >
                  <input
                    type="file"
                    accept='image/*'
                    name='image'
                    onChange={handleChange}
                  />
                </InputButton>

                <InputButton            

                >

                  <input 
                    type='file'
                    accept='application/pdf'
                    name='resume'
                    onChange={handleChange}
                  />
                </InputButton>

                <Typography
                  variant='h6'
                  className={classes.skills}
                >
                  skills
                </Typography>

                {
                  profileInput.skills.map( (skill, index) => {

                    return (
                        <>
                          <TextField 
                            label='skill name'
                            name='skill_name'
                            value={skill.name}
                            onChange={ e => handleChange(e, index)}
                            // error={error.error && error.index===index && error.label==='name'}
                            // error={true}

                          />
                          <TextField 
                            label='proficiency'
                            name='skill_proficiency'
                            value={skill.proficiency}
                            type='number'
                            // error={error.error && error.index===index && error.label==='proficiency'}
                            // helperText={error.error && error.index===index && error.label==='proficiency' ? error.text : ''}
                            onChange={
                              (e => handleNumber(e, index))
                            }
                          />
                        </>
                    )

                  })

                }
                <Button 
                  color='primary'
                  variant='contained'

                >

                Add another


                </Button>










              </Grid>
        </Grid>



      </DialogContent>

    </MuiDialog>
  )
}

export default Dialog
