import React from 'react'

import {Dialog, Button, Divider, DialogContent, DialogTitle} from '@material-ui/core'
import IconButton from '@material-ui/core/IconButton'
import Typography from '@material-ui/core/Typography'

import {makeStyles} from '@material-ui/core/styles'

import NavigateNextIcon from '@material-ui/icons/NavigateNext';
import NavigateBeforeIcon from '@material-ui/icons/NavigateBefore';

const useStyles = makeStyles(theme => ({
//  these styles will be applied to material-ui component
  root: {
    position: "absolute",
    top: 0,
    marginTop: 10,
  },

  dialogWidth: {
    maxWidth: 700,
    maxHeight: '100%',
    [theme.breakpoints.down('sm')]: {
      maxWidth: '100%',
      width: '100%'
    },
  },
  // 



  next: {
  },

  prev: {


  },


  action: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: theme.spacing(2),
  },

  slide: {
    height: 280,

    '& img': { 
      width: "100%",
      marginBottom: theme.spacing(1),
    },

    '& .next': {
      position: "absolute",
      top: '50%',
      right: 0
    },
    '& .prev': {

    }
  },

  info: {
    width: '90%',
    margin: 'auto',
    marginBottom: theme.spacing(1),

    '& .MuiTypography-h5': {
      fontWeight: theme.typography.fontWeightBold,
      fontSize: '2rem'
    },
    '& .MuiTypography-subtitle1': {
      fontWeight: theme.typography.fontWeightBold,
      opacity: 0.6,
    },
  },



}))


function ProjectDialog({openDetail, setOpenDetail}) {
  const classes = useStyles()
  const [slideImage, setSlideImage] = React.useState(0)

  const handleSilde = (e, name) => {
    e.preventDefault()
      if (name === 'next')
        setSlideImage(oldValue => {
          return ++oldValue
        })
      else 
        setSlideImage(oldValue => {
          return --oldValue
        })
  }
  console.log(
      "this is old value",
      slideImage
    )

  return (
    <Dialog 
      open={openDetail} 
      maxWidth="sm" 
      onClose={()=>setOpenDetail(false)} 
      classes={{paper: classes.root, paperWidthSm: classes.dialogWidth}} >
      <div
        className={classes.content}
        >
        <div className={classes.slide}> 
          {/* <img 
            src="https://lp-cms-production.imgix.net/news/2019/02/taj-mahal-monkey.jpg" 
           /> */}
          <div className={classes.next}>
            <IconButton 
              onClick={(e, name="next") => handleSilde(e, name)}
              >
              <NavigateNextIcon fontSize='large'/>
            </IconButton>
          </div>
          <div className={classes.prev}>
            <IconButton
              onClick={(e, name="prev") => handleSilde(e, name)}
              >
              <NavigateBeforeIcon fontSize="large" />
            </IconButton>
          </div>
        </div>
        <div className={classes.info}>
          <Typography 
          variant="h5"

           >
          Project Name
           </Typography>
        <Typography 
          variant="subtitle1"

        >
          this is a subtitle
        </Typography>
        <Divider/>
        <Typography
          variant="body1"
        >
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Tenetur delectus molestias labore, rerum, qui praesentium sunt quae, et veniam dolores esse iure sequi eos tempora.
        </Typography>
        <div className={classes.action}>
          <Button
            variant="contained"
            color="secondary"
            > 
            View Site
          </Button>

          <Button 
          color="secondary" 
          onClick={()=>setOpenDetail(false)}
          className={classes.closeButton}
          >
          X
        </Button>

        </div>
        </div>
      </div>

    </Dialog>
  )
}

export default ProjectDialog
