import React from 'react'


// component from material-ui
import {Paper, Box, Typography, Button } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'

import Particles from "react-tsparticles";


const useStyles = makeStyles(theme => ({
  root: {
    // background: theme.palette.primary,
    // height: props => props.windowHeightWidth.height,
  },
  typo: {
    [theme.breakpoints.down('xs')]: {
      fontSize: theme.spacing(3.5)
    },
  }
}))


function Home() {

  const [windowHeightWidth, setWindowHeightWidth] = React.useState({height: window.innerHeight, width: window.innerWidth})
  const props = {windowHeightWidth}
  const classes = useStyles(props)

  function resize() {
  setWindowHeightWidth({height: window.innerHeight, width: window.innerWidth})
}


  React.useEffect(()=>{
    window.addEventListener('resize', resize)
    return () => window.removeEventListener('resize', resize)
  }, [])


  return (
    <Paper className={classes.root} id="home">
      <Particles
        id="tsparticles"
        options={{
          background: {
            color: {
              value: "#0d47a1",
            },
          },
          fpsLimit: 60,
          interactivity: {
            detectsOn: "canvas",
            events: {
              onClick: {
                enable: true,
                mode: "push",
              },
              onHover: {
                enable: true,
                mode: "repulse",
              },
              resize: true,
            },
            modes: {
              bubble: {
                distance: 400,
                duration: 2,
                opacity: 0.8,
                size: 40,
              },
              push: {
                quantity: 4,
              },
              repulse: {
                distance: 200,
                duration: 0.4,
              },
            },
          },
          particles: {
            color: {
              value: "#ffffff",
            },
            links: {
              color: "#ffffff",
              distance: 150,
              enable: true,
              opacity: 0.5,
              width: 1,
            },
            collisions: {
              enable: true,
            },
            move: {
              direction: "none",
              enable: true,
              outMode: "bounce",
              random: false,
              speed: 6,
              straight: false,
            },
            number: {
              density: {
                enable: true,
                value_area: 800,
              },
              value: 80,
            },
            opacity: {
              value: 0.5,
            },
            shape: {
              type: "circle",
            },
            size: {
              random: true,
              value: 5,
            },
          },
          detectRetina: true,
        }}
      />
    <Box
    display="flex"
    flexDirection="column"
    justifyContent="center"
    alignItems="center"
    minHeight="100vh"
    style={{position: 'absolute', top: 0, left: 0, transform: 'translate(-50%, )'}}
    >
    <Typography variant="h3" align="center" className={classes.typo}>
     Hello, I'm <span style={{color:"red"}}>Osama Khan</span><br></br>I am a software developer
    </Typography>  
    <Button variant="outlined"  color="inherit">View my Work </Button>
  </Box>

    </Paper>
  )
}

export default Home
