import React from 'react'


// component from material-ui
import Paper from '@material-ui/core/Paper'
import makeStyles from '@material-ui/core/styles/makeStyles'



const useStyles = makeStyles(theme => ({
  root: {
    backgroundImage: 'url(https://images.unsplash.com/photo-1612151855475-877969f4a6cc?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1489&q=80)',
    backgroundSize: 'cover',
    height: props => props.windowHeightWidth.height,
    width: props => props.windowHeightWidth.width,
    backgroundPosition: 'center',
    position: 'relative',
  }
}))


function Home() {

  const [windowHeightWidth, setWindowHeightWidth] = React.useState({height: window.innerHeight, width: window.innerWidth})
  const props = {windowHeightWidth}
  const classes = useStyles(props)
  console.log(classes)

  function resize() {
  setWindowHeightWidth({height: window.innerHeight, width: window.innerWidth})
}


  React.useEffect(()=>{
    window.addEventListener('resize', resize)
    return () => window.removeEventListener('resize', resize)
  }, [])


  return (
    <Paper className={classes.root}>
      this is a pper



    </Paper>
  )
}

export default Home
