import React from 'react'

import Tabs from '@material-ui/core/Tabs'
import Tab from '@material-ui/core/Tab'
import Divider from '@material-ui/core/Divider'





const navLinks = [
  {name: 'Home', link:'#home'},
  {name: 'About', link: '#about'},
  {name: 'Projects', link: '#projects'},
  // {name: 'Blog', link: '#blog'},
  {name: 'Contact', link: '#contact'},
]


function NavLinks(props) {
  const [currentTab, setCurrentTab] = React.useState(0)

  function handleScroll(e) {
    console.log(window.pageYOffset)
    console.log(document.getElementById('about').getBoundingClientRect())
    console.log(window.scrollY)
      // console.log(window.pageYOffset)
    let i =0;
    for(i; i<navLinks.length; i++) {
      let link = navLinks[i].link.substring(1)
     const rect = document.getElementById(link).getBoundingClientRect()

     if ((window.pageYOffset >= rect.top) && (window.pageYOffset <= rect.top+window.scrollY + rect.height)) {
      setCurrentTab(i)
      break
     }
    }
  }


  React.useEffect(()=>{
    window.addEventListener('scroll', handleScroll)

    return () => window.removeEventListener('scroll', handleScroll)
  },[])


  return (
    <Tabs 
      value={currentTab}
      textColor="primary"
      onChange={(e,value)=> setCurrentTab(value)}
      {...props}
    >
     {
       navLinks.map((navLink, index) => {
         return (
          <Tab key={index} label={navLink.name} href={navLink.link} />
         )
       })
     }
   </Tabs>
  )
}

export default NavLinks
