import React from 'react'
// materila-ui component

import IconButton from '@material-ui/core/IconButton'
import {Drawer,Tab, Tabs} from '@material-ui/core'
import makeStyles from '@material-ui/core/styles/makeStyles';


// icons 
import MenuOpenIcon from '@material-ui/icons/MenuOpen';
// import MenuCloseIcon from '@material-ui/icons/MenuCloseIcon';

const useStyles = makeStyles(theme => ({
  paper: {
    top: props => props.drawerPosition
  }
}))




function MobileView() {
  const [openDrawer, setOpenDrawer] = React.useState(false)
  const [drawerPosition, setDrawerPosition] = React.useState(0)

  React.useEffect(() => {
  const rect = document.getElementById('appBar').getBoundingClientRect()
  setDrawerPosition(rect.top + rect.height)
  }, [])

  const props = {
    drawerPosition,
  }

  const classes = useStyles(props)


  return (
    <>
      <IconButton 
        onClick={()=>setOpenDrawer()}
        >
        <MenuOpenIcon fontSize="large" />
      </IconButton>
    <Drawer
    anchor="left"
    open={openDrawer}
    onClose={()=>setOpenDrawer(false)}
    classes={{paper: classes.paper}}
    variant='persistent'
     >
     <Tabs
        value={1}
        orientation="vertical"
        // variant="fullwidth"
        // indicatorColor="inherit"
        style={{width:"240px"}}
      >
        <Tab label="home" />
        <Tab label="about" />
        <Tab label="project" />
        <Tab label="Contact" />
      </Tabs>

  </Drawer>  
    </>
  )
}

export default MobileView
