import React from 'react'
import Home from './component/Home'
import Header from './component/Header'
import About from './component/about/About'
import Project from './component/projects/Projects'

import {Paper, useMediaQuery} from '@material-ui/core'

import {ThemeProvider, createMuiTheme, useTheme} from '@material-ui/core/styles'
import {GlobalContext} from './ContextApi'

function App() {
    const {darkMode} = GlobalContext()
    const userTheme = createMuiTheme ({
    palette: {
      type: darkMode ? "dark": "light",
      background: {
        light: "#f9fbe7",
        dark: "#424242;"
      }
    }
  })
  const theme = useTheme()
  console.log(useMediaQuery(theme.breakpoints.down('xs')))
  return (
    <ThemeProvider theme={userTheme}>
        {theme.breakpoints.down('xs') ? 
          <Paper>
          <Home />
          <Header />
          <About />
          <Project />
        </Paper> : 
        <div>skin is smaller</div>

    }
    </ThemeProvider>

  )
}

export default App
